import hatenahaiku4j.util.*import dao.*def now = DateUtil.nowdef param = [	'service'	: 'haiku',	'priority'	: 50,	'createdAt'	: now,	'who'		: 'fumoinfoboard',	'command'	: 'reply',	'inReplyToStatusId'	: '9259264389227871833',	'text'		: """\返信のテストですよ。優先度は50です。コマンド登録時刻は${HatenaUtil.formatDate(now)}です。"""]new CommandQueueDao().addCommand(param)html.html {	head {		title 'create Entry Command Test'	}	body {		h1 'create fumoinfoboard entry command'		table(border: 1) {			tr {				th 'サービス'				td param.service			}			tr {				th '誰宛'				td param.who			}			tr {				th 'コマンドの種類'				td param.command			}			tr {				th '返信元'				td param.inReplyToStatusId			}			tr {				th 'キーワード'				td param.keyword			}			tr {				th '投稿内容'				td param.text			}			tr {				th '優先度'				td param.priority			}		}	}}